def analyze_mis(name, mask, edge_index):
    selected_nodes = [i for i, m in enumerate(mask) if m]
    num_selected = len(selected_nodes)
    print(f"\n{name} Analysis:")
    print(f"Selected {num_selected} nodes: {selected_nodes}")
    
    # Build Adjacency
    adj = {i: set() for i in range(data['num_nodes'])}
    for u, v in zip(edge_index[0], edge_index[1]):
        adj[u].add(v)
        adj[v].add(u) # undirected

    violations = []
    for i in range(len(selected_nodes)):
        for j in range(i + 1, len(selected_nodes)):
            u = selected_nodes[i]
            v = selected_nodes[j]
            if v in adj[u]:
                violations.append((u, v))
    
    if violations:
        print(f"INVALID: Found {len(violations)} violations (connected nodes in set).")
        print(f"Violations: {violations}")
        return False, num_selected
    else:
        print("VALID: Independent Set.")
        return True, num_selected

print(f"Analyze Graph 275 (N={data['num_nodes']})")
valid_true, size_true = analyze_mis("True Pointers", data['true_mask'], data['edge_index'])
valid_pred, size_pred = analyze_mis("Predicted Pointers", data['predicted_mask'], data['edge_index'])

print("\n--- Summary ---")
print(f"Ground Truth: Size={size_true}, Valid={valid_true}")
print(f"Prediction:   Size={size_pred}, Valid={valid_pred}")

if valid_pred and valid_true:
    if size_pred < size_true:
        print("Result: Model UNDER-PREDICTED the size.")
    elif size_pred > size_true:
        print("Result: Model found a LARGER set than Ground Truth! (Ground Truth is sub-optimal?)")
    else:
        print("Result: Model perfectly matched the size.")
elif not valid_pred:
    print("Result: Model output is INVALID (contains edges).")
